/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import MinimalExample from '../src/components/MinimalExample';
import MinimalExampleSource from '!raw-loader!../src/data/MinimalExample.txt'
import Preview from '../src/Preview';
import MinimalHandleExample from '../src/components/MinimalHandleExample';
import MinimalHandleExampleSource from '!raw-loader!../src/data/MinimalHandleExample.txt' 
import MinimalLoadingExample from '../src/components/MinimalLoadingExample';
import MinimalLoadingExampleSource from '!raw-loader!../src/data/MinimalLoadingExample.txt';
import MinimalErrorExample from '../src/components/MinimalErrorExample';
import MinimalErrorExampleSource from '!raw-loader!../src/data/MinimalErrorExample.txt';
import MinimalOptionsExample from '../src/components/MinimalOptionsExample';
import MinimalOptionsExampleSource from '!raw-loader!../src/data/MinimalOptionsExample.txt';
import MinimalProgressExample from '../src/components/MinimalProgressExample';
import MinimalProgressExampleSource from '!raw-loader!../src/data/MinimalProgressExample.txt';
import DashboardExample from '../src/components/DashboardExample';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "why"
    }}>{`Why?`}</h1>
    <p>{`This goal of this package is to take away the pain of manually handling fetching states (idle, loading, error)
for every one of your components. Just hand over an `}<inlineCode parentName="p">{`AxiosResponse`}</inlineCode>{` object to Fetcher and let it handles the
visual feedbacks and the what-ifs.`}<br parentName="p"></br>{`
`}{`This is especially usefull for building SaaS and PaaS with numerous independant and small components.`}<br parentName="p"></br>{`
`}{`(e.g a Sales Card, Message Box, Activities Card, etc)`}</p>
    <h1 {...{
      "id": "installation"
    }}>{`Installation`}</h1>
    <h2 {...{
      "id": "using-yarn"
    }}>{`Using yarn`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`yarn add use-fetcher-react
`}</code></pre>
    <h2 {...{
      "id": "using-npm"
    }}>{`Using npm`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`npm install use-fetcher-react
`}</code></pre>
    <h1 {...{
      "id": "usage"
    }}>{`Usage`}</h1>
    <p><inlineCode parentName="p">{`use-fetcher-react`}</inlineCode>{` exposes a component `}<inlineCode parentName="p">{`Fetcher`}</inlineCode>{` that you are supposed to wrap around your content.`}<br parentName="p"></br>{`
`}{`That components handle automatically fetching and feedbacks rendering.  `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`import React from 'react';
import { Fetcher } from 'use-fetcher-react';

const MyComponent = () => {
  return (
    <div>
      <Fetcher>
        My content
      </Fetcher>
    </div>
  );
}
`}</code></pre>
    <p>{`But this won't do much on its own.`}<br parentName="p"></br>{`
`}<inlineCode parentName="p">{`use-fetcher-react`}</inlineCode>{` also exposes `}<inlineCode parentName="p">{`useFetcher`}</inlineCode>{` which returns a `}<inlineCode parentName="p">{`FetcherAPI`}</inlineCode>{` object. You can interact with it
inside your component and any visual feedback will be handled by `}<inlineCode parentName="p">{`Fetcher`}</inlineCode>{`, with the condition
that you pass the API as a prop.  `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`import React, { useEffect } from 'react';
import { useFetcher, Fetcher } from 'use-fetcher-react';
import axios from 'axios';

const MyComponent = () => {
  const fetcher = useFetcher();
  useEffect(() => {
    let request = axios.get('...');
    fetcher.fetch(request, data => {
      // Do something with data
    });
  }, []);

  return (
    <div>
      <Fetcher fetcher={fetcher}>
        My content
      </Fetcher>
    </div>
  );
}
`}</code></pre>
    <p>{`There's no need for handling errors on this level. If any happens,
the callback won't be called and a visual feedback will be displayed.`}</p>
    <h2 {...{
      "id": "minimal-example"
    }}>{`Minimal Example`}</h2>
    <p>{`Here's a minimal example of a component's fetching being handled by `}<inlineCode parentName="p">{`Fetcher`}</inlineCode>{`.`}<br parentName="p"></br>{`
`}{`The component `}<strong parentName="p">{`doesn't`}</strong>{` have to react to the HTTP response nor does it have to show any loading feeback.`}<br parentName="p"></br>{`
`}{`All of that is handled by `}<inlineCode parentName="p">{`Fetcher`}</inlineCode>{` automatically.  `}</p>

    <Preview mdxType="Preview">
  <MinimalExample mdxType="MinimalExample" />
  <editor>{MinimalExampleSource}</editor>
    </Preview>
    <h2 {...{
      "id": "custom-promise"
    }}>{`Custom Promise`}</h2>
    <p><inlineCode parentName="p">{`FetcherAPI.fetch`}</inlineCode>{` expects an axios promise. You can however let Fetcher handle any type of promise with `}<inlineCode parentName="p">{`FetcherAPI.handle`}</inlineCode>{`. `}</p>

    <Preview mdxType="Preview">
  <MinimalHandleExample mdxType="MinimalHandleExample" />
  <editor>{MinimalHandleExampleSource}</editor>
    </Preview>
    <h2 {...{
      "id": "fetching-states"
    }}>{`Fetching States`}</h2>
    <h3 {...{
      "id": "loading"
    }}>{`Loading`}</h3>
    <p>{`When `}<inlineCode parentName="p">{`fetcher.fetch`}</inlineCode>{` is called, the component automatically enters the loading state.`}<br parentName="p"></br>{`
`}{`But you can manually set it:  `}</p>

    <Preview mdxType="Preview">
  <MinimalLoadingExample mdxType="MinimalLoadingExample" />
  <editor>{MinimalLoadingExampleSource}</editor>
    </Preview>
    <h3 {...{
      "id": "error"
    }}>{`Error`}</h3>
    <p>{`Likewise, the error state can be enforced manually:  `}</p>

    <Preview mdxType="Preview">
  <MinimalErrorExample mdxType="MinimalErrorExample" />
  <editor>{MinimalErrorExampleSource}</editor>
    </Preview>  
When an error is encountered while fetching resources, `Fetcher` displays an 
error message as well as a button to retry the request. All of these can be customized as described bellow.
    <h2 {...{
      "id": "options"
    }}>{`Options`}</h2>
    <p>{`When using `}<inlineCode parentName="p">{`useFetch`}</inlineCode>{`, an options object can be passed as parameter.  `}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Option`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default Value`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`errorMessage`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The error message to display when an error is caught.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`An error occured`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`minDelay`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`number`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Minimal time to wait, in ms before collecting the request's response. useful to display proper loading feedback to the user.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`500`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`loadingColor`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The color of the loading spinner.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#36d7b7`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`buttonComponent`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(props: { doRetry: () => void }) => JSX.Element`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A React component to render a custom button component.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`null`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`loaderComponent`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(props: { color: string}) => JSX.Element`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A React component to render a custom spinner.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`null`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`errorComponent`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(props: { options: FetcherOptions}) => JSX.Element`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A React component to render a custom error visual feedback. Note that if this is used, `}<inlineCode parentName="td">{`buttonComponent`}</inlineCode>{` will be ignored.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`null`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`wrapperClassCSS`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`className`}</inlineCode>{` value of the `}<inlineCode parentName="td">{`Fetcher`}</inlineCode>{` container.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`fetcher-wrapper`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`loadingClassCSS`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`className`}</inlineCode>{` value of the element wrapping the loading spinner.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`fetcher-loading`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`errorClassCSS`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`className`}</inlineCode>{` value of the element wrapping the error feedback.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`fetching-error`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`wrapperStyles`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`React.CSSProperties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A style object to apply to the wrapper element.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`loadingStyles`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`React.CSSProperties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A style object to apply to the loading element.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`errorStyles`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`React.CSSProperties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A style to apply to the error element.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`progress.show`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Display a progress bar while fetching? Default is `}<inlineCode parentName="td">{`false`}</inlineCode>{`.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`progress.color`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The color of the progress bar.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#36d7b7`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`progress.errorColor`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The color of the progress bar when an error happens.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#cd3f45`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`progress.tickDelay`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{ min: number, max: number}`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`In the loading state, the progress bar increases at each tick. This parameter defines how`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`frequently ticks happen.  A random value is selected each time between `}<inlineCode parentName="td">{`min`}</inlineCode>{` and `}<inlineCode parentName="td">{`max`}</inlineCode>{`.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{ min: 500, max: 1000}`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`progress.tickValue`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{ min: number, max: number}`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Defines how much the bar grows at each tick.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{ min: 2, max: 3}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`progress.styles`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`React.CSSProperties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`A style object to apply the progress bar element.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "example"
    }}>{`Example`}</h3>
    <h4 {...{
      "id": "custom-button-error-message-and-loading-color"
    }}>{`Custom button, error message and loading color`}</h4>

    <Preview mdxType="Preview">
  <MinimalOptionsExample mdxType="MinimalOptionsExample" />
  <editor>{MinimalOptionsExampleSource}</editor>
    </Preview>  
    <h4 {...{
      "id": "progress-bar"
    }}>{`Progress bar`}</h4>

    <Preview mdxType="Preview">
  <MinimalProgressExample mdxType="MinimalProgressExample" />
  <editor>{MinimalProgressExampleSource}</editor>
    </Preview>  
    <h4 {...{
      "id": "global-options"
    }}>{`Global Options`}</h4>
    <p>{`I recommend to define your Fetcher options as ES modules and export them throughout your
applications instead of defined them for each of your component that need some customization.  `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`import { myFetcherOptions } from '@/utils/fetcherOptions';

// In your components
const fetcher = useFetcher(myFetcherOptions);
`}</code></pre>
    <h2 {...{
      "id": "customizing-css"
    }}>{`Customizing CSS`}</h2>
    <p>{`Modify the following CSS classes:`}</p>
    <ul>
      <li parentName="ul">{`fetcher-wrapper  `}</li>
      <li parentName="ul">{`fetcher-error`}</li>
      <li parentName="ul">{`fetching-loading`}</li>
    </ul>
    <h2 {...{
      "id": "dashboard-example"
    }}>{`Dashboard Example`}</h2>

    <Preview mdxType="Preview">
  <DashboardExample mdxType="DashboardExample" />
  <editor>{MinimalOptionsExampleSource}</editor>
    </Preview>  
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
