import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import 'bulma/css/bulma.css';
import '../../src/index.css';
import 'react-fetcher-hooks/dist/styles.css';
import Preview from '../../src/Preview.tsx';
import UseFetcherExample from '../../src/components/UseFetcherExample';
import UseFetcherExampleSource from '!raw-loader!../../src/components/UseFetcherExample.tsx';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "usefetcher"
    }}>{`UseFetcher`}</h1>
    <p>{`It is the most flexible hook but the most verbose. It serves as a building block for all
the other hooks.`}</p>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <h3 {...{
      "id": "usefetcher-1"
    }}>{`useFetcher`}</h3>
    <p>{`Create a simple fetcher reference.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`const ref = useFetcher();
`}</code></pre>
    <h3 {...{
      "id": "fetch"
    }}>{`fetch`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.fetch(request: () => AxiosPromise, onResult: data => void)`}</inlineCode><br parentName="p"></br>{`
`}{`Perform a request.`}</p>
    <Preview tabNames={['JavaScript', 'TypeScript']} mdxType="Preview">
  <editor>
        {`ref.fetch(() => axios.get('users'), data => {
  // do something with data
});
`}
  </editor>
  <editor>
        {`ref.fetch(() => axios.get<User[]>('users'), data => {
  // do something with data
  // data is of type User[]
});
`}
  </editor>
    </Preview>
    <h3 {...{
      "id": "fetchmany"
    }}>{`fetchMany`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.fetch(request: () => AxiosPromise[], onResult: data => void)`}</inlineCode><br parentName="p"></br>{`
`}{`Perform multiple axios requests at once.`}</p>
    <Preview tabNames={['JavaScript', 'TypeScript']} mdxType="Preview">
  <editor>
        {`ref.fetch(() => [axios.get('users'), axios.get('cats')], data => {
  // do something with data
});
`}
  </editor>
  <editor>
        {`ref.fetch(() => [axios.get<User[]>('users'), axios.get<Cat[]>('cats')], data => {
  // do something with data
  // data is of type [User[], Cat[]]
});
`}
  </editor>
    </Preview>
    <h3 {...{
      "id": "handle"
    }}>{`handle`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.handle(request: () => Promise, onResult: data => void)`}</inlineCode><br parentName="p"></br>{`
`}{`Handle anything, as long as it returns a `}<inlineCode parentName="p">{`Promise`}</inlineCode>{`.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`const doSomething = () => {
  return new Promise((resolve, reject) => {
    // Wait 3s
    setTimeout(() => {
      resolve('foo');
    }, 3000);
  });
};
`}</code></pre>
    <Preview tabNames={['JavaScript', 'TypeScript']} mdxType="Preview">
  <editor>
        {`ref.handle(() => doSomething(), data => {
  // data is 'foo'
});
`}
  </editor>
  <editor>
        {`ref.handle(() => doSomething(), data => {
  // data is 'foo' and its type is infered
});
`}
  </editor>
    </Preview>
    <h3 {...{
      "id": "setloading"
    }}>{`setLoading`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.setLoading(value: boolean)`}</inlineCode><br parentName="p"></br>{`
`}{`Mnually trigger the loading state.  `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`ref.setLoading(true);
ref.setLoading(false);
`}</code></pre>
    <h3 {...{
      "id": "seterror"
    }}>{`setError`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.setLoadingsetError(message: string, allowRetry: boolean)`}</inlineCode><br parentName="p"></br>{`
`}{`Manually trigger the error state.  `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`ref.setError('Oops!');
ref.setError('Something went wrong', true);
`}</code></pre>
    <h3 {...{
      "id": "cancel"
    }}>{`cancel`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.cancel()`}</inlineCode><br parentName="p"></br>{`
`}{`Cancel the ongoing request. What that means is that the `}<inlineCode parentName="p">{`onResult`}</inlineCode>{` callback will not be called if the request
ever returns.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`ref.cancel();
`}</code></pre>
    <h3 {...{
      "id": "retry"
    }}>{`retry`}</h3>
    <p><inlineCode parentName="p">{`FetcherRef.retry()`}</inlineCode><br parentName="p"></br>{`
`}{`Will try again an ongoing request that has failed.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`ref.retry();
`}</code></pre>
    <h2 {...{
      "id": "example"
    }}>{`Example`}</h2>

    <Preview mdxType="Preview">
  <UseFetcherExample mdxType="UseFetcherExample" />
  <editor>{UseFetcherExampleSource}</editor>
    </Preview>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
